    @Test
    public void appendWatermark() throws Exception {
        // 待加水印的文件
        PdfReader reader = new PdfReader("D:\\work\\融资租赁\\百望\\333333\\test.pdf");
        // 加完水印的文件
        PdfStamper stamper = new PdfStamper(reader, new FileOutputStream("D:\\work\\融资租赁\\百望\\333333\\test-watermark.pdf"));


        int total = reader.getNumberOfPages() + 1;
        PdfContentByte waterMar;
        // 设置水印透明度
        PdfGState gs = new PdfGState();
        // 设置笔触字体不透明度为0.4f
        gs.setStrokeOpacity(0.4f);

        // 设置字体
        BaseFont font = BaseFont.createFont();
        // 循环对每页插入水印
        for (int i = 1; i < total; i++) {
            // 水印的起始
            waterMar = stamper.getUnderContent(i);
            // 开始
            waterMar.beginText();
            Image image = Image.getInstance("D:\\work\\融资租赁\\百望\\333333\\db_logo.png");
            // 设置坐标 绝对位置 X Y
            image.setAbsolutePosition(200, 300);
            // 设置旋转弧度
            image.setRotation(30);// 旋转 弧度
            // 设置旋转角度
            image.setRotationDegrees(45);// 旋转 角度
            // 设置等比缩放
            image.scalePercent(90);// 依照比例缩放
            // image.scaleAbsolute(200,100);//自定义大小
            // 设置透明度
            waterMar.setGState(gs);
            // 添加水印图片
            waterMar.addImage(image);
            // 设置透明度
            waterMar.setGState(gs);
            //结束设置
            waterMar.endText();
            waterMar.stroke();
        }
        stamper.close();
    }

